parameters.jsCode:
// Parse Discord message for task creation
const discordMessage = $json.content;
const author = $json.author;
const channelId = $json.channel_id;

// Extract task details using regex patterns
const taskMatch = discordMessage.match(/\/assign @(\w+) #([A-Z]+-\d+) "([^"]+)"/i);
const statusMatch = discordMessage.match(/\/status update #([A-Z]+-\d+) "([^"]+)"/i);
const createMatch = discordMessage.match(/\/create-task "([^"]+)" @(\w+) (High|Medium|Low)/i);

let taskType = 'unknown';
let taskData = {};

// Determine task type and extract data
if (taskMatch) {
  taskType = 'assign';
  taskData = {
    assignee: taskMatch[1],
    ticketId: taskMatch[2],
    description: taskMatch[3],
    author: author.username,
    channelId: channelId
  };
} else if (statusMatch) {
  taskType = 'status_update';
  taskData = {
    ticketId: statusMatch[1],
    statusMessage: statusMatch[2],
    author: author.username,
    channelId: channelId
  };
} else if (createMatch) {
  taskType = 'create';
  taskData = {
    summary: createMatch[1],
    assignee: createMatch[2],
    priority: createMatch[3],
    author: author.username,
    channelId: channelId
  };
}

// Map Discord channels to Jira projects
const channelMapping = {
  'team-a-jira-development': 'N8N-JIRA',
  'team-b-cms-research': 'N8N-CMS',
  'team-c-outreach': 'N8N-OUT',
  'team-d-analytics': 'N8N-ANA'
};

const projectKey = channelMapping[channelId] || 'N8N';

return {
  taskType,
  taskData,
  projectKey,
  originalMessage: discordMessage
};

