// Weekly aggregation trigger check
const shouldTriggerWeeklyAnalysis = new Date().getDay() === 1; // Monday

return {
  prospect_summary: {
    id: data.prospect.id,
    stage: data.buying_intelligence.stage,
    urgency: data.buying_intelligence.urgency_level,
    primary_pain: data.pain_points.primary_concerns[0] || 'Unknown'
  },
