  trend_data: trendInsights,
  trigger_weekly_analysis: shouldTriggerWeeklyAnalysis,
  next_steps: {
    immediate: data.recommended_actions.immediate_followup,
    content_needed: data.recommended_actions.content_suggestions,
    positioning_updates: data.recommended_actions.positioning_adjustments
  }
};

