// Enhanced pain point extraction using sales guru frameworks
const interaction = $json;
const timestamp = new Date().toISOString();

// April Dunford's positioning framework analysis
const duffordAnalysis = {
  competitiveAlternatives: [],
  uniqueAttributes: [],
  valueConcerns: [],
  targetMarketFit: '',
  marketCategory: '',
  trendRelevance: []
};

// Extract conversation text
const conversationText = interaction.message || interaction.email_body || interaction.form_response || '';

// Identify pain point categories
const painPointCategories = {
  automation_complexity: 0,
  cost_justification: 0,
  technical_barriers: 0,
  time_constraints: 0,
  change_management: 0,
  integration_challenges: 0,
  competitive_pressure: 0,
  skill_gap: 0
};

// Emotional indicators
const emotionalIndicators = {
  frustration_level: 0, // 1-10 scale
  urgency_level: 0,     // 1-10 scale
  confidence_level: 0,  // 1-10 scale
  budget_sensitivity: 0 // 1-10 scale
};

// Prepare for AI analysis
const analysisPrompt = `
Analyze this prospect interaction for pain points using the April Dunford positioning framework:

Conversation: "${conversationText}"

Extract:
1. COMPETITIVE ALTERNATIVES: What are they currently using or considering instead?
2. UNIQUE ATTRIBUTES: What capabilities do they wish they had?
3. VALUE CONCERNS: What benefits are they skeptical about?
4. TARGET MARKET FIT: Do they see themselves as an ideal customer?
5. MARKET CATEGORY: How do they categorize this type of solution?
6. TREND RELEVANCE: What current trends are driving their interest?

Also identify:
- Specific pain point quotes (exact language used)
- Emotional indicators (frustration, urgency, confidence)
- Stage in buying journey (awareness, consideration, decision)
- Primary objections or concerns
- Budget/timeline constraints mentioned

Output as structured JSON.
`;

return {
  interaction_id: `${timestamp}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: timestamp,
  source: interaction.source || 'unknown',
  prospect_id: interaction.prospect_id || 'anonymous',
  conversation_text: conversationText,
  analysis_prompt: analysisPrompt,
  initial_categories: painPointCategories,
  emotional_baseline: emotionalIndicators,
  positioning_framework: duffordAnalysis,
  metadata: {
    platform: interaction.platform,
    conversation_length: conversationText.length,
    interaction_type: interaction.type
  }
};

